const projectLabels = $('Get Project from Moco').first().json.labels;
function projectHasLabel(label)
{
    return projectLabels.indexOf(label) > -1;
}

let account_no = 3000; // Aufträge (Material und Arbeit)

if (projectHasLabel("ZEV")) {
    account_no = 3010; // Erlös Stromproduktion
}
if (projectHasLabel("Stromproduktion")) {
    account_no = 3010; // Erlös Stromproduktion
}
if (projectHasLabel("Dienstleistung")) {
    account_no = 3400; // Dienstleistungen Solaranlagen
}
if (projectHasLabel("Beratung")) {
    account_no = 3410; // Beratung Energie-Management
}
if (projectHasLabel("Wartung")) {
    account_no = 3450; // Service und Wartung
}
if (projectHasLabel("Personalverleih")) {
    account_no = 3480; // Dienstleistungen Personalverleih
}

return {"account_no": account_no}

